DELIMITER //
CREATE FUNCTION contar_dtos(id_edificio BIGINT) RETURNS INT 
    BEGIN

    RETURN (SELECT COUNT(id_edificio = d.edificios_id_edificio) FROM departamentos d WHERE id_edificio = d.edificios_id_edificio && d.monto_renta >= 2000);

    END //
DELIMITER ;

CREATE VIEW departamentos_por_edificio_costo_mayor_2000 AS SELECT contar_dtos(e.id_edificio) AS cantidad_dtos, e.nombre, e.ubicacion FROM departamentos d JOIN edificios e WHERE d.edificios_id_edificio = e.id_edificio && d.monto_renta >= 2000;
SELECT * FROM departamentos_por_edificio_costo_mayor_2000;

