DELIMITER //
CREATE FUNCTION dto_mas_barato(id_edificio BIGINT) RETURNS BIGINT
    BEGIN

    SELECT MIN(d.monto_renta) INTO @mas_barato FROM departamentos d WHERE id_edificio = d.edificios_id_edificio;

    RETURN (SELECT d.id_departamento FROM departamentos d WHERE d.monto_renta = @mas_barato);

    END //
DELIMITER ;

CREATE VIEW obtener_dto_mas_barato AS 
SELECT e.nombre AS nombre_edificio, d.id_departamento, d.tamanio, d.numero_habitaciones, d.numero_banios, d.numero_dto, 
    d.monto_renta, d.servicios, d.estacionamiento 
FROM edificios e JOIN departamentos d WHERE d.id_departamento = dto_mas_barato(e.id_edificio);

