CREATE PROCEDURE aplicar_descuento_edificio(id_edificio BIGINT, descuento INT)
    BEGIN
    UPDATE departamentos SET monto_renta = monto_renta - (monto_renta * (descuento/100)) WHERE edificios_id_edificio = id_edificio;
    END //
